import React, { useState } from 'react';

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState('Marsya Putra');
  const [studentID, setStudentID] = useState('2702367220');
  const [email, setEmail] = useState('Marsya.putra@binus.ac.id');

  const handleSave = () => {
    setIsEditing(false);
    // You can add logic here to save the data to a database or state
    console.log('Saved:', { name, studentID, email });
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-b from-blue-500 to-red-400">
      <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-md">
        <div className="flex flex-col items-center">
          <img
            src="https://via.placeholder.com/100"
            alt="Profile"
            className="w-24 h-24 rounded-full border-4 border-blue-500 shadow-md"
          />
          {isEditing ? (
            <div className="mt-4 w-full">
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="w-full p-2 mb-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="text"
                value={studentID}
                onChange={(e) => setStudentID(e.target.value)}
                className="w-full p-2 mb-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full p-2 mb-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                onClick={handleSave}
                className="mt-2 w-full bg-gradient-to-r from-blue-500 to-red-400 text-white py-2 rounded-full shadow-md hover:from-blue-600 hover:to-red-500 transition duration-300"
              >
                Save
              </button>
            </div>
          ) : (
            <>
              <h2 className="mt-4 text-2xl font-semibold text-gray-800">{name}</h2>
              <p className="text-gray-500">Student ID: {studentID}</p>
              <p className="text-gray-500">{email}</p>
              <button
                onClick={() => setIsEditing(true)}
                className="mt-6 bg-gradient-to-r from-blue-500 to-red-400 text-white py-2 px-6 rounded-full shadow-md hover:from-blue-600 hover:to-red-500 transition duration-300"
              >
                Edit
              </button>
            </>
          )}
          <button
            onClick={() => window.location.href = '/'}
            className="mt-4 text-blue-500 hover:underline"
          >
            Back to Home
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
